INSERT INTO SWS_WRP_EVT (CD, WRP, WRPCD, OPTM, TTL, CNTT, LEV, CAT) VALUES ('2016122309475285050023330002', 'RSR', '50023330002', '2012-12-01 00:00:00.000', '重庆市发展和改革委员会通过渝发改农20121930号《关于忠县金鸡水库工程可行性研究报告的批复》对该工程进行批复', '重庆市发展和改革委员会通过渝发改农20121930号《关于忠县金鸡水库工程可行性研究报告的批复》对该工程进行批复', '1', NULL);
